        public function Front1Jan(Request $request){
            $alloffront1jan= [];
            $monitorings = Monitoring::orderBy('problem_date', 'DESC');
            if($request->provider = "Front"){
                $monitorings->where('provider', 'LIKE', "%". "Front". "%" );
            }
            if($request->problem_date = date(1)){
                $monitorings->whereDay('problem_date', '=', date(1) );
            }
            if($request->problem_date = date("Jan")){
                $monitorings->whereMonth('problem_date', '=', '01' );
            }
            if($request->problem_date = date("Jan")){
                $monitorings->whereMonth('problem_date', '=', '01' );
            }
            $monitorings = $monitorings->get();
            foreach ($monitorings as $mn)
            array_push($this->arrayfront1jan, $mn->problem_dur);
            $this->front1jancount = array_sum($this->arrayfront1jan);
            return View::make('front1jan', ['monitorings' => $monitorings,  'front1jancount' => $this->front1jancount, 'alloffront1jan' => $alloffront1jan]);
        }
        
